package com.Practice30days;

import java.util.Scanner;

public class Day5_Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        
        for(int j=1;j<=10;j++)
        {
        	System.out.println(n+" x "+j+" = "+n*j);
        }
	}
//upload
}
